'20:7': [
{sup:"1a", word:"first", vrefs:"Luke 24:1; 1&nbsp;Cor. 16:2", lines:["The Lord&rsquo;s Day ({Rev1:10|Rev. 1:10}). Paul stayed in Troas for seven days ({Act20:6|v. 6}), but it was only on the first day of the week that they gathered together to break bread in remembrance of the Lord. This indicates that at that time the apostle and the church considered the first day of the week a day to meet together for the Lord."]},
{sup:2, word:"we", lines:["Including the writer, Luke."]},
{sup:"b", word:"break", vrefs:"Acts 20:11; 2:42, 46; 1&nbsp;Cor. 10:16; 11:23-24"},
],
