'20:22': [
{sup:1, word:"bound", lines:["See {Act20:3^2|note 3<sup>2</sup>}."]},
{sup:2, word:"spirit", lines:["Paul&rsquo;s regenerated spirit, in which he served God (see {Act17:16^1|note 16<sup>1</sup>} in {Act17|ch. 17}). In his spirit, a spirit joined to the Lord the Spirit ({1Co6:17|1&nbsp;Cor. 6:17}), Paul sensed beforehand that something would happen to him in Jerusalem, and the Holy Spirit testified this to him ({Act20:23|v. 23})."]},
{sup:"a", word:"Jerusalem", vrefs:"Acts 20:16; 21:15"},
{sup:3, word:"there", lines:["Lit., in it."]},
],
'20:23': [
{sup:1, word:"Except", lines:["Paul did not know what he would encounter in Jerusalem ({Act20:22|v. 22}), but he did know one thing: the Holy Spirit was solemnly testifying to him that bonds and afflictions awaited him. The Holy Spirit&rsquo;s testifying was only a prophecy, a foretelling, not a charge. Hence, he must not have taken it as a command but as a warning. See notes {Act21:4^1|4<sup>1</sup>} and {Act21:11^1|11<sup>1</sup>} in {Act21|ch. 21}."]},
{sup:"a", word:"Spirit", vrefs:"Acts 21:4, 11"},
{sup:"b", word:"bonds", vrefs:"Acts 21:33"},
{sup:"c", word:"afflictions", vrefs:"Rom. 8:35; 1&nbsp;Thes. 3:3"},
],
'20:24': [
{sup:1, word:"consider", lines:["Implying that Paul sensed he was going to be martyred."]},
{sup:"2a", word:"life", vrefs:"Acts 15:26; 21:13", lines:["Or, soul."]},
{sup:"b", word:"course", vrefs:"2&nbsp;Tim. 4:7; Heb. 12:1"},
{sup:"c", word:"ministry", vrefs:"Acts 1:17; 21:19; Rom. 11:13; 2&nbsp;Cor. 4:1"},
{sup:"d", word:"testify", vrefs:"Acts 2:40; 22:18"},
{sup:"e", word:"gospel", vrefs:"Acts 15:7"},
{sup:"f", word:"grace", vrefs:"Acts 20:32; 4:33; Gal. 2:21"},
],
