'7:3': [
{sup:1, word:"preserve", lines:["The ark was not only for the salvation of man but also for the salvation of all living creatures. See {Col1:20|Col. 1:20} and {Col1:20^2|note 2}; {Heb2:9|Heb. 2:9} and {Heb2:9^3|note 3}; and {Mrk16:15|Mark 16:15} and {Mrk16:15^2|note 2}."]},
],
'7:4': [
{sup:"1a", word:"forty", vrefs:"Gen. 7:12, 17", lines:["Forty is the number for trials, temptations, and sufferings ({Heb3:9|Heb. 3:9}; {Mat4:2|Matt. 4:2}; {1Ki19:8|1&nbsp;Kings 19:8})."]},
],
