'48:20': [
{sup:"a", word:"Come", vrefs:"Isa. 52:11; Jer. 50:8; 51:6; Zech. 2:6-7; Rev. 18:4"},
{sup:1, word:"Babylon", lines:["In {Rev17|Rev. 17} and {Rev18|18} Babylon has two aspects: the religious aspect, signifying the Roman Catholic Church, and the material, physical aspect, signifying the city of Rome, the capital of Antichrist&rsquo;s kingdom (see {Rev18:2^1|note 2<sup>1</sup>} in {Rev18|Rev. 18}). God&rsquo;s people need to come out of Babylon in its two aspects ({Rev18:4|Rev. 18:4})."]},
{sup:"b", word:"servant", vrefs:"Isa. 41:8"},
],
