'1:35': [
{sup:"a", word:"early", vrefs:"Luke 4:42"},
{sup:"1b", word:"prayed", vrefs:"Matt. 14:23; Luke 5:16", lines:["To fellowship with God, seeking God&rsquo;s will and pleasure for His gospel service. The Slave-Savior performed the evangelistic service not by Himself, independent of God and according to His own will, but according to God&rsquo;s will and pleasure by being one with God to fulfill His purpose (see {Mrk1:38^2|note 38<sup>2</sup>})."]},
],
'1:38': [
{sup:"a", word:"elsewhere", vrefs:"Luke 4:43"},
{sup:"1b", word:"preach", vrefs:"Isa. 61:1", lines:["See {Mrk1:14^2|note 14<sup>2</sup>}."]},
{sup:2, word:"this", lines:["As the Slave of God, the Slave-Savior served God in His gospel to carry out not His own will or the people&rsquo;s proposal but the will of God, who had sent Him ({Joh6:38|John 6:38}; {Joh4:34|4:34})."]},
{sup:"c", word:"came", vrefs:"cf. John 16:28"},
],
