'10:35': [
{sup:"1a", word:"Rise", vrefs:"Psa. 68:1-2", lines:["{Num10:35|Numbers 10:35} is quoted in {Psa68:1|Psa. 68:1}. In {Eph4:8|Eph. 4:8}-{Eph4:10|10} Paul applied the word in {Psa68|Psa. 68} to the ascension of Christ. Hence, the rising up in this verse refers to Christ&rsquo;s rising up to the heavens in His ascension. See {Psa68:1^1|note 1<sup>1</sup>} in {Psa68|Psa. 68}."]},
],
'10:36': [
{sup:1, word:"Return", lines:["Since the rising up in {Num10:35|v. 35} refers to Christ&rsquo;s rising up to the heavens in His ascension (see {Num10:35^1|note 35<sup>1</sup>}), the word return spoken by Moses in this verse must refer to the second coming of Christ. Moses&rsquo; word in vv. {Num10:35|35}-{Num10:36|36} portrays a full view of God&rsquo;s economy from Christ&rsquo;s incarnation to be the Ark, the embodiment of the Triune God, through His ascension to His second coming."]},
{sup:2, word:"Jehovah", lines:["Or, Jehovah of the ten thousands, to the thousands of Israel."]},
],
