'91:1': [
{sup:1, word:"dwells", lines:["{Psa91|Psalm 91} concerns the saints&rsquo; identification with Christ in His taking God as His dwelling place (see {Psa91:9^1|note 9<sup>1</sup>}). In their identification with Christ, the saints make Jehovah the Most High their habitation, dwelling in His secret place and abiding in His shadow under His wings (vv. {Psa91:1|1}-{Psa91:9|9}). This is the genuine oneness with God. Here, we are constituted with Him, and we and God live together as one."]},
{sup:"a", word:"secret", vrefs:"Psa. 27:5; 31:20; Isa. 32:2"},
{sup:"b", word:"shadow", vrefs:"Psa. 36:7; 121:5"},
{sup:2, word:"Almighty", lines:["Heb. Shaddai. See {Gen17:1^2|note 1<sup>2</sup>} in {Gen17|Gen. 17}."]},
],
